import React, { useState } from "react";

import { Container, Grid, Typography, Paper, TextField, IconButton } from "@mui/material";
import { styled } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Avatar, Link, Stack, Step } from "@mui/material";
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Divider from '@mui/material/Divider';
import DeleteIcon from '@mui/icons-material/Delete';



const styles ={
    sectionHeading:{
        fontFamily:"Arial",
        fontWeight: "700",
        color: "#CCC",
        borderBottom:"2px solid #8c363e",
        borderWidth:"20%",
        textAlign:"center"
    },
    sectionText:{
        marginTop: "5px",
        fontSize: "16px",
        color: "#CCC",
        letterSpacing: "0.5px",
    },
    sectionHeadBtmLine:{
        width: "60px",
        height: "3px !important",
        backgroundColor: "#a43f49",
        margin: "10px auto",
    },
    cardFeatureImage:{
        borderRadius:"5px",
        maxHeight:"250px",

    },
    section:{
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        padding: "20px",
        color:"#FFF",
        borderRadius:"5px",
        minHeight:"600px",
    },
    cardStyle:{
        width:"33%",
    } 
}
const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: 'transparent',
    ...theme.typography.body2,
    padding: theme.spacing(5),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));


const Todo = () =>{
    return <>
    <Grid item container spacing={2} justifyContent="center" style={styles.section}>
        
        <Grid container justifyContent="center">
            <Item sx={6} style={styles.cardStyle}>
                <Card>
                    <CardActionArea>
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="h2">
                                To Do Application
                            </Typography>
                            <Typography gutterBottom variant="subtitle1" component="h2">
                                Task Name
                            </Typography>
                            <TextField
                                error
                                id="outlined-error-helper-text"
                                label="Error"
                                defaultValue="Hello World"
                                helperText="Incorrect entry."
                                variant="outlined"
                            />
                            <Button  variant="contained" color="secondary">
                            Add Task
                            </Button>

                        </CardContent>
                    </CardActionArea>
                     
                </Card>
            </Item>
            <Item xs={6}>
                <Card>
                    <CardActionArea>
                        <CardContent>
                        <Typography gutterBottom variant="h5" component="h2">
                            Task List
                        </Typography>
                         <Grid>
                            <IconButton edge="end" aria-label="delete">
                                  <DeleteIcon />
                            </IconButton>
                         </Grid>
                        </CardContent>
                    </CardActionArea>
                     
                </Card>
            </Item>
        </Grid>
    </Grid>


    </>
}
export default Todo;
