import React, { useState } from "react";

import { Container, Grid, Typography, Paper } from "@mui/material";
import { styled } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Avatar, Link, Stack, Step } from "@mui/material";
// import FeaturePhoto1 from "../images/feature_1.png";
import ProfileImage from '../images/img1.jpg'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});
//Custom Components

const styles ={
    sectionHeading:{
        fontFamily:"Arial",
        fontWeight: "700",
        color: "#fff",
        // borderBottom:"2px solid #8c363e",
        borderWidth:"20%",
    },
    sectionText:{
        marginTop: "5px",
        fontSize: "16px",
        color: "#fff",
        letterSpacing: "0.5px",
    },
    sectionHeadBtmLine:{
        width: "60px",
        height: "3px !important",
        backgroundColor: "#a43f49",
        margin: "10px auto",
    }
}
const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: 'transparent',
    ...theme.typography.body2,
    padding: theme.spacing(5),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

  

const About = () =>{
    const classes = useStyles();
    return <>
        
            <Item sx={{ pt:20, pb:20, p:5, pr:5  }}>
                <Typography style={styles.sectionHeading} variant="h4">About Me</Typography>
                <Item style={styles.sectionHeadBtmLine}></Item>
                <Typography color="white" style={styles.sectionText} variant="subtitle1">
                This is a Bootstrap v4.2.1 CSS Template for you. Edit and use this layout for your site. Updated on 21 May 2019 for repeated main menu HTML code.
                </Typography>              
            </Item>
            <Item sx={{ pt:20, pb:20, p:5, pr:5  }}>
                <Grid item xs={6}>
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image=""
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Lizard
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
            across all continents except Antarctica
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
                </Grid>
                <Grid item xs={6}>

                </Grid>             
            </Item>
     </>
}
export default About;
