import React, { useState } from "react";
import "../css/style.css";

import { Container, Grid, Typography, Paper } from "@mui/material";
import { styled } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Avatar, Link, Stack, Step } from "@mui/material";
// import FeaturePhoto1 from "../images/feature_1.png";
import FeatureImage1 from '../images/feature_1.png';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Divider from '@mui/material/Divider';
//Components of Mono Stakced Bar
import MonoStackedBar from "mono-stacked-bar"
import "mono-stacked-bar/dist/index.css"


const styles ={
    sectionHeading:{
        fontFamily:"Arial",
        fontWeight: "700",
        color: "#fff",
        borderBottom:"2px solid #8c363e",
        borderWidth:"20%",
        textAlign:"center"
    },
    sectionText:{
        marginTop: "5px",
        fontSize: "16px",
        color: "#fff",
        letterSpacing: "0.5px",
    },
    sectionHeadBtmLine:{
        width: "60px",
        height: "3px !important",
        backgroundColor: "#a43f49",
        margin: "10px auto",
    },
    cardFeatureImage:{
        borderRadius:"5px",
        maxHeight:"250px",

    },
    media: {
        height: 250,
      },
    skillData:{
        
        backgroundColor: "rgba(0, 0, 0, 0.5)",
        padding: "20px",
        color:"#FFF",
    },
}
const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: 'transparent',
    ...theme.typography.body2,
    padding: theme.spacing(5),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }));

  

const Skills = () =>{
    const colors = ["#393986", "#6767aa", "#56b5b5"]

    return <>
        <Item sx={{ mt:5, pt:20, pb:20, p:5, pr:5  }}>
            <Typography style={styles.sectionHeading} variant="h4">What are my Skills</Typography>
            <span  style={styles.sectionHeadBtmLine}></span>

            <Typography color="white" style={styles.sectionText} variant="subtitle1">
                This is a Bootstrap v4.2.1 CSS Template for you. Edit and use this layout for your site. Updated on 21 May 2019 for repeated main menu HTML code.
            </Typography>   
        </Item>
        <Grid style={styles.skillData}>
            <p>Most simple</p>
            <MonoStackedBar data={[10, 20, 15]} />
            <p>Custom colors</p>
            <MonoStackedBar
                data={[
                { value: 10, color: "#fd4949" },
                { value: 20, color: "#fba73b" }
                ]}
            />
            <p>Without custom unit</p>
            <MonoStackedBar data={[50, 20, 30]} unit="%" colors={colors} />
            <p>Without label</p>
            <MonoStackedBar
                data={[10, 10, 10]}
                displayLabels={false}
                colors={colors}
            />
            <p>With caption</p>
            <MonoStackedBar
                data={[
                { value: 12, caption: "Men" },
                { value: 14, caption: "Women" },
                { value: 4, caption: "Other gender" }
                ]}
                colors={colors}
            />
            <p>Without radius + height + maxWidth</p>
            <MonoStackedBar
                data={[10, 10, 10]}
                radius={0}
                height={30}
                width={400}
                colors={colors}
            />
        </Grid>
        <MonoStackedBar data={[10, 20, 15]} />
    </>
}
export default Skills;
